import React, { Component } from 'react';
import axios from '../../axios';
import Posts from './Posts/Posts';
import './Blog.css';

// react 16.6 and higher => use Lazy for Lazy Loading =>




import { Route, NavLink, Switch, Redirect } from 'react-router-dom';
// import NewPost from '../Blog/NewPost/NewPost';
import asyncComponent from '../../HOC/asyncComponent';

// dynamic import syntax - imported
const AsyncNewPost = asyncComponent(() => {
  return import('../Blog/NewPost/NewPost');
});

class Blog extends Component {
  state = {
    auth: true,
  };
  render() {
    return (
      <div className="Blog">
        <header>
          <nav>
            <ul>
              <li>
                {/* set activeClassName to change class name for active link, use NavLink */}
                <NavLink
                  to="/posts/"
                  exact
                  activeClassName="my-active"
                  activeStyle={{
                    color: '#fa923f',
                    textDecoration: 'underline',
                  }}
                >
                  Posts
                </NavLink>
              </li>
              <li>
                <NavLink
                  to={{
                    // absolute path
                    pathname: '/newPost',
                    // relative path: pathname: this.props.match.url + '/newPost';
                    hash: '#submit',
                    search: '?quick-submit=true',
                  }}
                >
                  New Post
                </NavLink>
              </li>
            </ul>
          </nav>
        </header>
        <Switch>
          {this.state.auth ? (
            <Route path="/newPost" component={AsyncNewPost} />
          ) : null}
          {/* <Route path="/" exact render={() => <h1>Home</h1>} /> */}
          <Route path="/posts" component={Posts} />
          {/* this is a flexible path  below*/}
          {/* <Route path="/:id" exact component={FullPost} /> */}
          {/* path: '/:' will be replaced dynamically */}
          {/* lines 58-59 - redirect in case of restricted access or wrong address */}
          {/* <Redirect from="/" to="/posts" /> */}
          <Route render={() => <h1>Not found</h1>} />
        </Switch>
      </div>
    );
  }
}

export default Blog;
